function counter(e,t){this.counterDate=new Date(e),this.container=document.getElementById(t),this.update()}counter.prototype.calculateUnit=function(e,t){var i=Math.abs(i=e/t)<1?0:i;return Math.abs(0>i?Math.ceil(i):Math.floor(i))},counter.prototype.calculate=function(){var e=Math.abs(Math.round((new Date-this.counterDate)/1e3));this.days=this.calculateUnit(e,86400),this.hours=this.calculateUnit(e-86400*this.days,3600),this.mins=this.calculateUnit(e-86400*this.days-3600*this.hours,60),this.secs=this.calculateUnit(e-86400*this.days-3600*this.hours-60*this.mins,1)},counter.prototype.update=function(){this.calculate(),this.container.innerHTML='<p id="days"><strong>'+this.days+"</strong>"+(1==this.days?"day":"days")+'</p><p id="hours"><strong>'+this.hours+"</strong>"+(1==this.hours?"hour":"hours")+'</p><p id="mins"><strong>'+this.mins+"</strong>"+(1==this.mins?"min":"mins")+'</p><p id="secs"><strong>'+this.secs+"</strong>"+(1==this.secs?"sec":"secs")+"</p>";var e=this;setTimeout(function(){e.update()},1e3)};
